CustomField.formula:
IF($RecordType.Name == 'Technical Issue', if( DATEVALUE(HON_Commit_Date__c) >TODAY() ,'Green','Red' ), 
if( ispickval( X4_Hour_AOG__c ,'4-hour') &&( now() -CreatedDate >=0.123 ) && ( now() -CreatedDate <0.166), 'Yellow', 
if( ispickval( X4_Hour_AOG__c ,'4-hour') && (now() -CreatedDate >=0.166), 'Red', 
if( ispickval( X4_Hour_AOG__c ,'24-hour') && (now() -CreatedDate >=0.75 ) && (now() -CreatedDate <1), 'Yellow', 
if( ispickval( X4_Hour_AOG__c ,'24-hour') && (now() -CreatedDate >=1), 'Red', 
if( ispickval( X4_Hour_AOG__c ,'48-hour') && (now() -CreatedDate >=1.5 ) && (now() -CreatedDate <2), 'Yellow', 
if( ispickval( X4_Hour_AOG__c ,'48-hour') && (now() -CreatedDate >=2), 'Red', 
'Green') 
) 
) 
) 
) 
) 
)
