WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/19.0/connection.js")}

var Case_Num = '{!Case.CaseNumber}';
var conmail = '{!Contact.Primary_Email_Address__c}';
var webmail = '{!Case.SuppliedEmail}';
var attlist = sforce.connection.query("SELECT id,BodyLength, name FROM Attachment WHERE ParentId = '{!Case.Id}' and BodyLength>=3200000");
var rec = attlist.getArray("records");
if (!(conmail == null || conmail == '' || conmail == ' ')){
webmail = conmail ;
}
if (webmail == null || webmail == '' || webmail == ' '){
alert("There is no mailaddress associated with this Case.");
}
else{
if(rec.length>0){
var r = confirm("Attachments of Case will not be moved from Salesforce to SAP because of 3MB size limit. Please upload them manually.");
if(r==true){
window.open('https://myaerospace2.honeywell.com/onlineordering/singleSignOn?goto=EO&email='+webmail+'&caseId='+Case_Num+'&langid=-1');
}
else{
window.location.reload();
}
}
else{
window.open('https://myaerospace2.honeywell.com/onlineordering/singleSignOn?goto=EO&email='+webmail+'&caseId='+Case_Num+'&langid=-1');
}
}
