var conid='{!Contact.Id}'; 
var result=sforce.connection.query("Select Supported_Product_Name__c from Skills2__c where Contact__c='"+ conid+"' and (Expertise__c ='3-Advanced' or Expertise__c ='4-Expert' )"); 
var records1 = result.getArray('records'); 
var text;
for (i = 0; i < records1.length; i++) { 
if(text != null){
    text = text+escape(records1[i].Supported_Product_Name__c) + ",";
}else{
text =escape(records1[i].Supported_Product_Name__c) + ",";
}
}
parent.window.location.replace ('/00O13000008sG9k?pv0='+text);
