WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/29.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/29.0/apex.js")}

var res;
var Agmt = sforce.connection.query("SELECT Id,echosign_dev1__Recipient__c,echosign_dev1__Contract__c,echosign_dev1__Status__c FROM echosign_dev1__SIGN_Agreement__c WHERE echosign_dev1__Contract__c = '{!Contract.Id}' AND echosign_dev1__Status__c = 'Signed' limit 1");
var rec = Agmt.getArray("records"); 
if(rec.length>0){ 
res = sforce.apex.execute("sendAgmtNotification","sendNotification",{contracid:"{!Contract.Id}"});
    
if (res == "true") { 
    alert("Successfully sent the signed agreement."); 
    window.location.reload(); 
} 
else { 
    alert("There is a problem with sending signed agreement.Try Again."); 
}
}
else{

alert("There is no completed agreement associated with this Contract");

}
